import React, { Component } from 'react';
import './App.css';
import Header from './components/header/Header.js';
import Button from './components/button/Button.js'
import Input from './components/input/Input.js'

class App extends Component {
  constructor(props){
    super(props);
    this.handleInputChange = this.handleInputChange.bind(this);
    this.state = {childVals : []};
  }

  handleInputChange(childId, value){
    let childArr = this.state.childVals.slice();
    childArr[childId] = value;
    this.setState({ childVals : childArr });
  }

  onClickBtn = () => {
    let numbers = this.state.childVals['calculator_numberInput'];
    alert(numbers);
  }

  render() {
//    const {sharedConstant} = this.state;

    return (
      <div className="App">
      <div className="mdl-layout mdl-js-layout">
        <Header />

        <main className="mdl-layout__content" style="width:300px;">
          <div className="page-content">
            <Input callback={this.handleInputChange} childId="calculator_numberInput"/>
            <Button show="Button name" callback={this.onClickBtn}/>
          </div>
        </main>
        </div>

      </div>
    );
  }
}

export default App;
