import React, { Component } from 'react';
import './App.css';
import Header from './components/header/Header.js';
import Button from './components/button/Button.js'
import Input from './components/input/Input.js'
import CircularButton from './components/button/CircularButton.js'

class App extends Component {
  constructor(props){
    super(props);
    this.handleInputChange = this.handleInputChange.bind(this);
    this.state = {childVals : []};
  }

  handleInputChange(childId, value){
    let childArr = this.state.childVals.slice();
    childArr[childId] = value;
    this.setState({ childVals : childArr });
  }

  onClickBtn = (childProps) => {
    let numbers = this.state.childVals['calculator_numberInput'];
    alert(numbers);
  }

  onClickWriteNumber = (childProps) => {
    let number = childProps.show;
    alert(number);
  }

  render() {
    return (
      <div className="App">
      <div className="mdl-layout mdl-js-layout">
        <Header />

        <main className="mdl-layout__content">
          <div className="page-content">

            <Input callback={this.handleInputChange} childId="calculator_numberInput"/>

            <Button show="0" callback={this.onClickWriteNumber}/>
            <Button show="1" callback={this.onClickWriteNumber}/>
            <Button show="2" callback={this.onClickWriteNumber}/>
            <Button show="3" callback={this.onClickWriteNumber}/>
            <Button show="4" callback={this.onClickWriteNumber}/>
            <Button show="5" callback={this.onClickWriteNumber}/>
            <Button show="6" callback={this.onClickWriteNumber}/>
            <Button show="7" callback={this.onClickWriteNumber}/>
            <Button show="8" callback={this.onClickWriteNumber}/>
            <Button show="9" callback={this.onClickWriteNumber}/>

            <CircularButton show="+" />
            <CircularButton show="-" />

            <br/>
            <br/>
            <Button show="Button name" callback={this.onClickBtn}/>
          </div>
        </main>
        </div>

      </div>
    );
  }
}

export default App;
