import React, { Component } from 'react';
import './App.css';
import Header from './components/header/Header.js';
import Button from './components/button/Button.js';
import Input from './components/input/Input.js';
import CircularButton from './components/button/CircularButton.js';
import calc from './Calculator.js';

class App extends Component {

  constructor(props){
    super(props);
    this.setState({childVals : [], calc: calc(), operation: ""});
    this.operation = this.operation.bind(this);
    this.onClickWriteNumber = this.onClickWriteNumber.bind(this);
  }

  render() {
    const inputValue = this.getInputVal();

    return (
      <div className="App">
      <div className="mdl-layout mdl-js-layout">
        <Header />

        <main className="mdl-layout__content">
          <div className="page-content">

            <Input childId="calculator_numberInput" value={inputValue}/>

            <div id="result"> {this.state.calc.result}</div>

            <Button show="1" value="1" callback={this.onClickWriteNumber}/>
            <Button show="2" value="2" callback={this.onClickWriteNumber}/>
            <Button show="3" value="3" callback={this.onClickWriteNumber}/>
            <Button show="4" value="4" callback={this.onClickWriteNumber}/>
            <Button show="5" value="5" callback={this.onClickWriteNumber}/>
            <Button show="6" value="6" callback={this.onClickWriteNumber}/>
            <Button show="7" value="7" callback={this.onClickWriteNumber}/>
            <Button show="8" value="8" callback={this.onClickWriteNumber}/>
            <Button show="9" value="9" callback={this.onClickWriteNumber}/>
            <Button show="0" value="0" callback={this.onClickWriteNumber}/>

            <br/><br/>
            <CircularButton show="+" callback={this.operation}/>
            <CircularButton show="-" callback={this.operation}/>
            <CircularButton show="x" callback={this.operation}/>
            <CircularButton show="/" callback={this.operation}/>

            <br/>
            <br/>
            <Button show="=" callback={this.onClickBtn}/>
          </div>
        </main>
        </div>

      </div>
    );
  }

  //showing a val in input element
  showResult(result){
    this.saveChildValOnState("calculator_numberInput",result)
  }

  saveOperation(operation){
    this.saveChildValOnState("operation_", operation);
  }

  //getting input val
  getInputVal(){
    return this.state.childVals['calculator_numberInput'];
  }

  //saving value on state for child objects/elements
  saveChildValOnState(childId, value){
    let childArr = this.state.childVals.slice();
    let calc = this.state.calc;

    childArr[childId] = value;
    this.setState({ childVals : childArr, calc: calc});
  }

  onClickBtn = (value) => {
    this.operation("=");
  }

  onClickWriteNumber = (value) => {
    let numbers = this.getInputVal();
    if(numbers === undefined){
      numbers = "";
    }else{
      numbers += "";
    }
    this.showResult((numbers.concat(value)));
  }

  operation = (opType) => {
    let numbers = this.state.childVals['calculator_numberInput'];
    let lastOperation = this.state.childVals['operation_'];
    console.log("operation type : ", opType);

    switch (opType) {
      case '+':
          this.state.calc.addition(numbers);
          this.showResult("");
        break;
      case '-':
          this.state.calc.substraction(numbers);
          this.showResult("");
        break;
      case 'x':
          this.state.calc.multiplication(numbers);
          this.showResult("");
        break;
      case '/':
          this.state.calc.division(numbers);
          this.showResult("");
        break;
      case '=':
          console.log("when is = , lastoperation ", lastOperation);
          if(lastOperation !== '=')
            this.operation(lastOperation);
          else
            this.state.calc.addition("0");
            this.showResult("");
        break;
      default:
        break;
    }

    this.saveOperation(opType);
  }



}

export default App;
