import React, { Component } from 'react';
import $ from 'jquery';

class Input extends Component {
  constructor(props){
    super(props);
  }

  onChange(value){
    this.state.inputNumber = value;
  }

  render() {
    return (
      <div class="mdl-textfield mdl-js-textfield">
        <input class="mdl-textfield__input" type="text"
            pattern="-?[0-9]*(\.[0-9]+)?" id="sample2"
            onchange={()=>onChange($(this).val())}/>
        <label class="mdl-textfield__label" for="sample2">x number </label>
        <span class="mdl-textfield__error">is not a number</span>
      </div>
    );
  }
}

export default Input;
