import React, { Component } from 'react';
import './App.css';
import Header from './components/header/Header.js';
import Button from './components/button/Button.js'
import Input from './components/input/Input.js'

class App extends Component {
  constructor(props){
    super(props);
    this.handleInputChange = this.handleInputChange.bind(this);
    this.state = {inputNumber : 0};
  }

  handleInputChange(value){
    this.setState({ inputNumber : value });
  }

  onClickBtn = () => {
    let numbers = this.state.inputNumber;
    alert(numbers);
  }

  render() {
//    const {sharedConstant} = this.state;

    return (
      <div className="App">
      <div className="mdl-layout mdl-js-layout">
        <Header />

        <main className="mdl-layout__content">
          <div className="page-content">
            <Input callback={this.handleInputChange}/>
            <Button show="Button name" callback={this.onClickBtn}/>
          </div>
        </main>
        </div>

      </div>
    );
  }
}

export default App;
